mapper.select:
SELECT r.ri_id, r.ri_name, r.ri_person, r.ri_maxperson, r.ri_bed, r.ri_bedcnt, r.ri_bedroom, r.ri_bathroom, s.si_id, s.si_name, s.si_peak, s.si_off, s.si_discount, m.mi_id, m.mi_name, m.mi_phone FROM t_room_info r JOIN t_stay_info s ON r.si_id = s.si_id LEFT JOIN t_member_info m ON
m.mi_id = #{miId}
1 = 0
WHERE r.ri_id = #{riId} AND s.si_id = #{siId}

mapper.update:
UPDATE t_stay_reservation 
SET sr_status = 'c', sr_cancledate = SYSDATE, sr_paymentstatus = 'c'  
WHERE sr_id = #{id}
