mapper.select:
SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM t_stay_photo
WHERE
si_id = #{siId}
AND sp_idx = #{spIdx}


		AND ri_id IS NULL


		AND ri_id = #{riId}

mapper.update:
UPDATE t_stay_photo
SET sp_url = #{spUrl}
WHERE si_id = #{siId}
AND
sp_idx = #{spIdx}


		AND ri_id IS NULL


		AND ri_id = #{riId}
